"use strict";
//------------------------
// basic type annotations
//------------------------
let age = 90;
let firstName = 'John';
let isFictional;
// age = 'hello'
age = 88;
// firstName = 100
firstName = 'Jane';
// isFictional = 'false'
isFictional = false;
//----------------
// type inference
//----------------
let planet = 'Earth';
let moons = 1;
let isLarge = false;
// planet = 10
planet = 'Saturn';
// moons = '145'
moons = 145;
// isLarge = 'yes'
isLarge = true;
//------------------
// null & undefined
//------------------
let something;
let anotherThing;
// something = 10
something = null;
// anotherThing = 'hello, world'
anotherThing = undefined;
